import React, { Component } from 'react';
import logo from './logo.svg';
import { createRoot } from 'react-dom';
import { useFirestoreDocData, useFirestore, SuspenseWithPerf} from 'reactfire';
import './App.css';
import { FirebaseAppProvider } from 'reactfire';
import firebase from 'firebase';
import firebaseConf from './Firebase';

class App extends Component {

  constructor(props) {
    super(props);
    this.state = {
      form: [],
      alert: false,
      alertData: {}
    };
  }

  componentWillMount() {
    let formRef = firebaseConf.database().ref('form').orderByKey().limitToLast(6);
    formRef.on('child_added', snapshot => {
      const {email} = snapshot.val();
      const data = { email,};
      this.setState({ form: [data].concat(this.state.form) });
    })
  }


  myPlay(){
    const params = {
      email: "Apertei o Play",
      action: "play",
      created_at: Date(),
    };
  }

  myPause(){
    const params = {
      email: "Apertei o Pause",
      action: "play",
      created_at: Date(),
    };
  }

  sendMessage(e) {
    e.preventDefault();
    const params = {
      email: this.inputEmail.value,
      action: "Email Inserido",
      created_at: Date(),
    };
    if (params.email) {
      firebaseConf.database().ref('form').push(params).then(() => {
        this.showAlert('success', 'Your message was sent successfull');
      }).catch(() => {
        this.showAlert('danger', 'Você precisa inserir o Email!');
      });
      this.resetForm();
    } else {
      this.showAlert('warning', 'Você precisa inserir o Email!');
    };
  }

  render() {
    return (
          <center>
            <div id={"title"}><h1>Watch</h1></div>

            <div className='container'>
              <div className='col-md-12'>
                <div id="formulario">
                <form onSubmit={this.sendMessage.bind(this)} ref='contactForm' >
                  <div className='form-group'>
                    <input type='email' className='form-control' id='email' placeholder='Insira seu e-mail' ref={email => this.inputEmail = email} /><button type='submit' className='btn btn-primary'>Enviar</button>
                  </div>
                </form>
                  </div>
                <div id="video">
                <video onPlay={this.myPlay.bind(this)} onPause={this.myPause.bind(this)} id="myVideo" width="320" controls>
                  <source src="tecnologia.mp4" type="video/mp4" />
                </video>
                  </div>
              </div>
            </div>

            <div id={"author"}>Richard Nicson Costa de Lima</div>
          </center>
    );
  }
}
export default App;
