import React, { Component } from 'react';
import logo from './logo.svg';
import { createRoot } from 'react-dom';
import { useFirestoreDocData, useFirestore, SuspenseWithPerf} from 'reactfire';
import './App.css';
import { FirebaseAppProvider } from 'reactfire';
import firebase from 'firebase';
import firebaseConf from './Firebase';

class App extends Component {

  constructor(props) {
    super(props);
    this.state = {
      form: [],
      alert: false,
      alertData: {}
    };
  }

  showAlert(type, message) {
    this.setState({
      alert: true,
      alertData: { type, message }
    });
    setTimeout(() => {
      this.setState({ alert: false });
    }, 4000)
  }

  resetForm() {
    this.refs.contactForm.reset();
  }

  componentWillMount() {
    let formRef = firebaseConf.database().ref('form').orderByKey().limitToLast(6);
    formRef.on('child_added', snapshot => {
      const { name, email, city, phone, message } = snapshot.val();
      const data = { name, email, city, phone, message };
      this.setState({ form: [data].concat(this.state.form) });
    })
  }

  sendMessage(e) {
    e.preventDefault();
    const params = {
      email: this.inputEmail.value,
    };
    if (params.email) {
      firebaseConf.database().ref('form').push(params).then(() => {
        this.showAlert('success', 'Your message was sent successfull');
      }).catch(() => {
        this.showAlert('danger', 'Você precisa inserir o Email!');
      });
      this.resetForm();
    } else {
      this.showAlert('warning', 'Você precisa inserir o Email!');
    };
  }

  render() {
    return (
        <div>
          {this.state.alert && <div className={`alert alert-${this.state.alertData.type}`} role='alert'>
            <div className='container'>
              {this.state.alertData.message}
            </div>
          </div>}
          <div className='container' style={{ padding: `40px 0px` }}>
            <div className='row'>
              <div className='col-sm-4'>
                <form onSubmit={this.sendMessage.bind(this)} ref='contactForm' >
                  <div className='form-group'>
                    <input type='email' className='form-control' id='email' placeholder='Insira seu e-mail' ref={email => this.inputEmail = email} />
                  </div>
                  <button type='submit' className='btn btn-primary'>Enviar</button>
                </form>
              </div>
              <div className='col-sm-8'>
                <div className='row'>
                  {this.state.form.map(form =>
                      <div className='col-sm-6' key={form.phone} style={{ margin: `0px 0px 30px 0px` }}>
                        <div className='card'>
                          <div className='card-body'>
                            <a href={`mailto:${form.email}`} className='card-link'>{form.email}</a>
                          </div>
                        </div>
                      </div>)}
                </div>
              </div>
            </div>
          </div>
        </div>
    );
  }
}

export default App;
